import { Component, Input, ChangeDetectionStrategy, SecurityContext, SimpleChanges, OnChanges } from '@angular/core';
import { LanguageService } from '@app/services/language.service';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-twitter-widget',
  templateUrl: './twitter-widget.component.html',
  styleUrls: ['./twitter-widget.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class TwitterWidgetComponent implements OnChanges {
  @Input() handle: string;
  @Input() width = 300;
  @Input() height = 400;

  loading: boolean = true;
  error: boolean = false;
  lang: string = 'en';

  iframeSrc: SafeResourceUrl;

  constructor(
    private languageService: LanguageService,
    public sanitizer: DomSanitizer,
  ) {
    this.lang = this.languageService.getLanguage();
    this.setIframeSrc();
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes.handle) {
      this.setIframeSrc();
    }
  }

  setIframeSrc(): void {
    if (!this.handle) {
      return;
    }
    let url = `https://syndication.x.com/srv/timeline-profile/screen-name/${this.handle}?creatorScreenName=mempool`
          + '&dnt=true'
          + '&embedId=twitter-widget-0'
          + '&%3D%3D'
          + '&frame=false'
          + '&hideBorder=true'
          + '&hideFooter=false'
          + '&hideHeader=true'
          + '&hideScrollBar=false'
          + `&lang=${this.lang}`
          + '&maxHeight=500px'
          + '&origin=https%3A%2F%2Fmempool.space%2F'
          // + '&sessionId=88f6d661d0dcca99c43c0a590f6a3e61c89226a9'
          + '&showHeader=false'
          + '&showReplies=false'
          + '&siteScreenName=mempool'
          + '&theme=dark'
          + '&transparent=true'
          + '&widgetsVersion=2615f7e52b7e0%3A1702314776716';
    switch (this.handle.toLowerCase()) {
      case 'nayibbukele':
        url = 'https://bitcoin.gob.sv/twidget';
        break;
      case 'metaplanet_jp':
        url = 'https://metaplanet.mempool.space/twidget';
        break;
      default:
        break;
    }
    this.iframeSrc = this.sanitizer.bypassSecurityTrustResourceUrl(this.sanitizer.sanitize(SecurityContext.URL, url));
  }

  onReady(): void {
    this.loading = false;
    this.error = false;
  }

  onFailed(): void {
    this.loading = false;
    this.error = true;
  }
}
